import React, { useState, useEffect } from "react";
import AntButton from "./AntButton";
import { withFirebase } from "../Firebase";
import InputField from "./InputField";
import { withRouter } from "react-router-dom";
import { Alert, Button } from "antd";

const inputStyle = { width: "20vw" };

function ValidationField(props) {
  const [user, setUser] = useState("");
  const [pass, setPass] = useState("");
  const [error, setError] = useState(false);

  useEffect(() => {
    setError(false);
  }, [props.login]);

  useEffect(() => {
    const listener = (event) => {
      if (event.code === "Enter" || event.code === "NumpadEnter") {
        console.log("Enter key was pressed. Run your function.");
        // callMyFunction();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  }, []);

  function handleSubmit() {
    props
      .submit(user, pass)
      .then((res) => {
        props.history.replace("/dashboard");
      })
      .catch((err) => {
        setError(true);
      });
  }

  return (
    <form onSubmit={handleSubmit} style={{ textAlign: "center" }}>
      <span style={{ float: "left", marginLeft: "4vw" }}>
        <InputField
          type="user"
          changeFunc={(us) => {
            setError(false);
            setUser(us);
          }}
          style={inputStyle}
        ></InputField>
        <InputField
          type="pass"
          changeFunc={(us) => {
            setError(false);
            setPass(us);
          }}
          style={inputStyle}
        ></InputField>
      </span>
      <div style={{ paddingTop: "2vh", float: "left", paddingLeft: "2vw" }}>
        <AntButton submit={handleSubmit}></AntButton>
      </div>

      {error ? (
        <Alert message={props.errorMessage} type="error" showIcon />
      ) : null}
    </form>
  );
}

export default withRouter(withFirebase(ValidationField));
