import React from "react";
import { Button } from "antd";
import ValidationField from "./ValidationField";
import { withFirebase } from "../Firebase";
import { post } from "../Helpers";

function Register(props) {
  async function register(user, pass) {
    props.firebase.auth.signOut();
    props.firebase.doCreateUserWithEmailAndPassword(user, pass);
    let data = {
      userId: props.firebase.auth.currentUser.uid,
      amount: 10000000,
    };
    await post("http://localhost:8080/balances", data);
  }

  return (
    <div>
      <ValidationField
        submit={register}
        errorMessage={
          "Unable to create account. Check connection and try again."
        }
        login={false}
      ></ValidationField>
      <Button
        type="link"
        onClick={() => {
          props.setView(1);
        }}
      >
        Sign in
      </Button>
    </div>
  );
}

export default withFirebase(Register);
