import React, { useState } from "react";
import { withFirebase } from "../Firebase";
import Landing from "./Landing";
import Login from "./Login";
import Register from "./Register";
import Authentication from "./Authentication";

const pageStyle = { paddingTop: "40vh" };
const elementStyle = { display: "flex", justifyContent: "center" };

function Home() {
  const [view, setView] = useState(0);

  const views = [
    <Landing setView={setView}></Landing>,
    <Authentication
      setView={() => {
        setView(1);
      }}
      error="Username and/or password are incorrect. Try again."
      login
    ></Authentication>,
    <Authentication
      setView={() => {
        setView(2);
      }}
    ></Authentication>,
    <Login
      setView={setView}
      pageStyle={pageStyle}
      elementStyle={elementStyle}
    ></Login>,
    <Register
      setView={setView}
      pageStyle={pageStyle}
      elementStyle={elementStyle}
    ></Register>,
  ];

  return views[view];
}

export default withFirebase(Home);
