import React, { useState, useEffect } from "react";
import { get } from "../Helpers";
import { Card, Avatar } from "antd";
import PriceCounter from "./PriceCounter";

const priceStyle = { textAlign: "center", fontSize: "30px" };

export default function SearchResults(props) {
  const [stocks] = useState([]);
  const [profiles, setProfiles] = useState([]);

  useEffect(() => {
    get(
      "https://finnhub.io/api/v1/stock/symbol?exchange=US&token=bpleiinrh5r8m26im1dg"
    ).then((res) => {
      let r = [];
      for (let stock of res) {
        let symbol = stock["displaySymbol"];

        if (symbol.indexOf(props.searchText) === 0) {
          stocks.push(symbol);
        }
      }

      for (let i = 0; i < Math.max(5, stocks.length); i++) {
        r.push(
          get(
            "https://finnhub.io/api/v1/stock/profile2?symbol=" +
              symbol +
              "&token=bpleiinrh5r8m26im1dg"
          )
        );
      }

      Promise.all(r).then((vals) => {
        setProfiles(vals);
      });
    });
  }, []);

  function results() {
    if (!profiles) {
      return [];
    }
    let res = [];
    profiles.forEach((value, index) => {
      res.push(
        <a
          key={index}
          onClick={() => {
            props.setSelectedStock(stocks[index]);
          }}
        >
          <Card
            title={stocks[index] + " - " + value["name"]}
            extra={
              <Avatar
                shape="square"
                size={64}
                icon={<img src={value["logo"]}></img>}
              />
            }
            style={{ width: 300 }}
            hoverable
          >
            <PriceCounter
              ticker={stocks[index]}
              style={priceStyle}
            ></PriceCounter>
          </Card>
        </a>
      );
    });

    return res;
  }

  console.log(profiles);

  return <div>{results()}</div>;
}
